import React from 'react';
import './weather-rep.scss';
import Weather from "./weather";
import "bootstrap/dist/css/bootstrap.min.css";
import Form from "./form";

const API_KEY = "c6495ff8b8839c36a2991986616d8307";

// wolframwapha AppID=888UUR-X3TPQ8UGYP
//https://api.ipify.org?format=json
//curl https://ipapi.co/8.8.8.8/latlong/
//http://ip-api.com/json/24.48.0.1

class Weatherrep extends React.Component {
  constructor(){
    super();
    this.state={
      lat: undefined,
      long: undefined,
      city: undefined,
      country: undefined,
      icon: undefined,
      main: undefined,
      celsius: undefined,
      temp_max: undefined,
      temp_min: undefined,
      description: "",
      err: false, 
      ip:undefined,
    };
    this.weatherIcon={
      Thunderstorm: "wi-thunderstorm",
      Drizzle: "wi-sleet",
      Rain: "wi-storm-showers",
      Snow: "wi-snow",
      Atmosphere: "wi-fog",
      Clear: "wi-day-sunny",
      Clouds: "wi-day-fog"
    };
    this.getWeather();
  }

  get_WeatherIcon(icons,rangeId){
    switch(true){
      case rangeId >= 200 && rangeId <= 232:
        this.setState({icon: this.weatherIcon.Thunderstorm});
        break;
      case rangeId >= 300 && rangeId <= 321:
        this.setState({icon: this.weatherIcon.Drizzle});
        break;
      case rangeId >= 500 && rangeId <= 531:
        this.setState({icon: this.weatherIcon.Rain});
        break;
      case rangeId >= 600 && rangeId <= 622:
        this.setState({icon: this.weatherIcon.Snow});
        break;
      case rangeId >= 701 && rangeId <= 781:
        this.setState({icon: this.weatherIcon.Atmosphere});
        break;
      case rangeId === 800:
        this.setState({icon: this.weatherIcon.Clear});
        break;
      case rangeId >= 801 && rangeId <= 804:
        this.setState({icon: this.weatherIcon.Clouds});
        break;
      default:
        this.setState({icon: this.weatherIcon.clouds});

    }
  }

  calCelsius(temp){
    let cel = Math.floor(temp - 273.15);
    return cel;
  }

  getWeather = async(e) => {
    e.preventDefault();

    const ip_api_call = await fetch(`https://api.ipify.org?format=json`);
    const ip_response= await ip_api_call.json();
    console.log(ip_response);

    this.setState({
        //   city: `${response.name},${response.sys.country}`,
        //   lat: response.coord.lat,
        //   long: response.coord.lon,
        //   celsius: this.calCelsius(response.main.temp),
        //   temp_min: this.calCelsius(response.main.temp_min),
        //   temp_max: this.calCelsius(response.main.temp_max),
        //   description: response.weather[0].description,
        lat: undefined,
        long: undefined,
        city: undefined,
        country: undefined,
        icon: undefined,
        main: undefined,
        celsius: undefined,
        temp_max: undefined,
        temp_min: undefined,
        description: "",
        error: false,
        ip: ip_response.ip
        }); 
    const det_api_call = await fetch(`http://ip-api.com/json/${this.state.ip}`);
    const det_response= await det_api_call.json();
    console.log(det_response);

    // const city = 
    // const country = 
    // if( city && country ) {
    // const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}`);
    // const response= await api_call.json();
    // console.log(response);

    this.setState({
    //   city: `${response.name},${response.sys.country}`,
    //   lat: response.coord.lat,
    //   long: response.coord.lon,
    //   celsius: this.calCelsius(response.main.temp),
    //   temp_min: this.calCelsius(response.main.temp_min),
    //   temp_max: this.calCelsius(response.main.temp_max),
    //   description: response.weather[0].description,
    lat: undefined,
    long: undefined,
    city: undefined,
    country: undefined,
    icon: undefined,
    main: undefined,
    celsius: undefined,
    temp_max: undefined,
    temp_min: undefined,
    description: "",
    error: false,
    ip: ip_response.ip
    });
  };

  render() {
    return(
      <div className="App">
        <h1 className="App-header">  Weather App</h1>

        <Weather 
        city={this.state.city} 
        country={this.state.country}
        lat={this.state.lat} 
        long={this.state.long}
        temp_celsius={this.state.celsius}
        temp_min={this.state.temp_min}
        temp_max={this.state.temp_max}
        description={this.state.description}
        weatherIcon={this.state.icon}
        ip={this.state.ip}
        />  
      </div>
    );
  }
}


export default Weatherrep;
