import React from "react";
import "./news.css";
import "bootstrap/dist/css/bootstrap.min.css";
const News = ({title,image,description,url,content,author,time,name}) => {
    let newText = time.split('T');
    let newText1 = newText[1].split('Z')
    return(
        <div className="col s5">
            <div className="polaroid">
                <img src={image} class="img-fluid" alt="Responsive image" />
                <div className="row">
                    <div className="col s3">
                        <div className="container">
                            <i class="small material-icons">account_circle</i>
                            <h6><small>{author}</small></h6>
                        </div>
                        <div className="col s9">
                            {title? <b>{title}</b> : null} 
                            <h6>{name}</h6>
                            {time? <h6><small><b>Date: </b>{newText[0]}, <b>Time: </b>{newText1[0]}</small></h6> : null}
                        </div>
                    </div>
                </div>    
            </div>
        </div>
    );
}

export default News;

